INSERT INTO `bloglist` VALUES (1,'Top 10 Món Ăn Đường Phố Việt Nam','Danh sách những món ăn đường phố nổi tiếng và hấp dẫn tại Việt Nam.',1,'assets/img/product/mon_an_duong_pho.jpg','2025-03-12 19:27:07','Nguyễn Văn An'),(2,'Cách Pha Chế Trà Sữa Tại Nhà','Hướng dẫn chi tiết cách pha chế trà sữa đơn giản mà ngon như ngoài tiệm.',2,'assets/img/product/cach-pha-tra-sua.jpg','2025-03-12 19:27:07','Trần Thị Bích'),(3,'Bí Quyết Làm Bánh Mì Bơ Tỏi','Chia sẻ cách làm bánh mì bơ tỏi giòn ngon, thơm phức tại nhà.',3,'assets/img/product/canhlambanhmibo.png','2025-03-12 19:27:07','Lê Hữu Minh'),(4,'Những Món Hải Sản Nổi Tiếng Miền Trung','Khám phá các món hải sản đặc trưng của miền Trung Việt Nam.',4,'assets/img/product/dacsanvungmien.png','2025-03-12 19:27:07','Phạm Thị Lan'),(5,'Cà Phê Pha Phin - Văn Hóa Cà Phê Việt','Tìm hiểu về cách pha cà phê phin và văn hóa thưởng thức cà phê tại Việt Nam.',5,'assets/img/product/caphe_1.jpg','2025-03-12 19:27:07','Ngô Minh Hải'),(6,'Cách Làm Phở Bò Tại Nhà','Hướng dẫn nấu phở bò thơm ngon, chuẩn vị như ngoài hàng.',6,'assets/img/product/2-cach-nau-pho-bo-bang-goi-gia-vi.png','2025-03-12 19:27:07','Hoàng Thị Như Ý'),(7,'Bánh Chưng - Hương Vị Ngày Tết','Câu chuyện về bánh chưng và cách gói bánh đúng chuẩn.',7,'assets/img/product/banhtrung.jpg','2025-03-12 19:27:07','Vũ Minh Tuấn'),(8,'Món Ngon Từ Dừa','Khám phá các món ăn ngon làm từ dừa, đặc sản miền Tây.',8,'assets/img/product/dac-san-mien-tay-1.jpg','2025-03-12 19:27:07','Đặng Thanh Hương'),(9,'Cách Làm Bánh Flan Mịn Màng','Công thức làm bánh flan béo mịn, thơm ngon tại nhà.',9,'assets/img/product/cachlambanhflan.jpg','2025-03-12 19:27:07','Trần Văn Long'),(10,'Lẩu Thái Chua Cay','Cách nấu lẩu Thái chua cay ngon đúng vị.',10,'assets/img/product/lauthai.jpg','2025-03-12 19:27:07','Lê Văn Hùng'),(11,'Hướng Dẫn Làm Bánh Cuốn','Công thức làm bánh cuốn nóng hổi, thơm ngon.',11,'assets/img/product/cachlambanhcuon.png','2025-03-12 19:27:07','Nguyễn Thị Hằng'),(12,'Bún Bò Huế - Đặc Sản Miền Trung','Tìm hiểu về bún bò Huế và cách nấu ngon đúng điệu.',12,'assets/img/product/cachnaubunbohue.jpg','2025-03-12 19:27:07','Phạm Minh Đức'),(13,'Cách Làm Chè Khúc Bạch','Hướng dẫn cách làm chè khúc bạch thanh mát, hấp dẫn.',13,'assets/img/product/huongdannauchekhucbach.jpg','2025-03-12 19:27:07','Trần Thị Mai'),(14,'Gỏi Cuốn - Món Ăn Lành Mạnh','Cách làm gỏi cuốn tươi ngon, phù hợp với chế độ ăn healthy.',14,'assets/img/product/goi-cuon-thumb.jpg','2025-03-12 19:27:07','Lý Quốc Bảo'),(15,'Hướng Dẫn Làm Xôi Xéo','Công thức làm xôi xéo vàng ươm, dẻo thơm.',15,'assets/img/product/cachnauxoixeo.jpg','2025-03-12 19:27:07','Đinh Thị Huyền');
